import React, { useState } from "react";
import "./App.css";

import ExpenseList from "./components/ExpenseList";
import NewExpenseForm from "./components/NewExpenseForm";

let dummyExpences = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
  {
    id: "e5",
    title: "Book",
    amount: 9.12,
    date: new Date(2018, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: "e6",
    title: "Car",
    amount: 2940.67,
    date: new Date(2018, 2, 28),
  },
  {
    id: "e7",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2019, 5, 12),
  },
];

function App() {
  const [expenses, setExpenses] = useState(dummyExpences);

  const newExpenseSubmitHandler = (expense) => {
    setExpenses((prevExpenses) => {
      setExpenses([expense, ...dummyExpences]);
    });
  };
  return (
    <div className="App">
      <NewExpenseForm onSubmitNewExpense={newExpenseSubmitHandler} />

      <ExpenseList data={expenses} />
    </div>
  );
}

export default App;
